import React, { Component } from 'react'

export class About extends Component {

    render() {
        return (
            <>
            <div className="text-center5">
                <h2 className="text-center2 ">All In One News Aggregator</h2>
</div>
                <div className="text-center10">
                <p className='text-center3'>
                This is a <b>React</b> based News Application which is made with the help of <b>News Api </b>which is a simple, easy-to-use REST API that returns JSON search results for current and historic news articles published by over 80,000 worldwide sources.
                This App gathers news articles from the big and reputed news websites with the help of Api and display it to our users. Users can read headlines and click read more button and they are redirected to the website of the big news company
                where they can enjoy reading the news articles.
                </p>
                </div>
            </>

        )
    }
}


export default About
